        public static L2FlagBoxEnd[] GetItemGetFlags(ItemID id)
        {
            L2FlagBoxEnd[] result = null;
            getFlags.TryGetValue(id, out result);
            return result;
        }

        public static L2FlagBoxParent[] GetBoxOpenFlags(ItemID id)
        {
            L2FlagBoxParent[] result = null;
            openFlags.TryGetValue(id, out result);
            return result;
        }

        public static L2FlagBoxParent[] GetActiveItemFlags(ItemID id)
        {
            L2FlagBoxParent[] result = null;
            activeFlags.TryGetValue(id, out result);
            return result;
        }

        public static ItemData GetItemData(ItemID id)
        {
            ItemData result = null;
            itemData.TryGetValue(id, out result);
            return result;
        }

